import React, { Component } from 'react';
import Moment from "react-moment";
import './Time.scss';

class Time extends Component {
  componentDidMount() {
    var canvas = this.refs.canvas;
    canvas.width = 2000;
    canvas.height = 2000;
    canvas.style.width = "80%";
    canvas.style.height = "80%";

    const ctx = this.refs.canvas.getContext('2d');
    var radius = canvas.height / 4;
    ctx.scale(2, 2);
    ctx.translate(radius, radius);

    setInterval(function () {
      ctx.save();
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      drawNumbers(ctx, radius);
      drawTime(ctx, radius);
      ctx.restore();
    }, 1000);

    function drawNumbers(ctx, radius) {
      var ang;
      ctx.font = radius * 0.15 + "px arial";
      ctx.fillStyle = "white";
      ctx.textBaseline = "middle";
      ctx.textAlign = "center";
      var months = [
        "Jan",
        "•",
        "•",
        "Apr",
        "•",
        "•",
        "Jul",
        "•",
        "•",
        "Oct",
        "•",
        "•"
      ];
      months.forEach(function (month, num) {
        ang = num * Math.PI / 6;
        ctx.rotate(ang);
        ctx.translate(0, -radius * 0.75);
        ctx.rotate(-ang);
        ctx.fillText(month, 0, 0);
        ctx.rotate(ang);
        ctx.translate(0, radius * 0.75);
        ctx.rotate(-ang);
      });
    }

    function drawTime(ctx, radius) {
      var now = new Date();
      var nowMS = now.getTime();
      var startMS = new Date(new Date().getFullYear(), 0, 1).getTime();
      var endMS = new Date(new Date().getFullYear(), 11, 31).getTime();
      var totalMS = endMS - startMS;
      var elapsedMS = nowMS - startMS;
      var ratio = elapsedMS / totalMS;
      var degrees = 360 * ratio;
      var radians = degrees * (Math.PI / 180);
      radians = radians.toFixed(6);


      ctx.strokeStyle = "white";
      ctx.lineWidth = radius * 0.02;
      ctx.lineCap = "round";
      ctx.moveTo(0, 0);
      ctx.rotate(radians);
      ctx.lineTo(0, -radius * 0.9);
      ctx.stroke();
    }
  }

  render() {
    return (
      <div style={{ backgroundImage: `url(${this.props.image})`, backgroundColor: this.props.color }} className="module time">
        <div className="content">
          <div className="time">
            <Moment format="h:mm" /><span className="ampm"><Moment format="A" /></span>
          </div>
          <div className="date">
            <Moment format="dddd, MMMM Do" />
          </div>
          <div className="clock"><canvas ref="canvas"></canvas></div>
        </div>
      </div>
    );
  }
}

export default Time;
