import React, { Component } from 'react';
import 'normalize.css';
import './App.scss';
import Weather from './modules/Weather.js';
import Time from './modules/Time.js';
import Calendar from './modules/Calendar.js';
import Feed from './modules/Feed.js';
import Subway from './modules/Subway.js';
import io from "socket.io-client";

class App extends Component {
  state = {
    background: ""
  }

  componentDidMount() {
    if (process.env.NODE_ENV === "development") {
      this.setState({socket: io("localhost:3000")});
    } else {
      this.setState({socket: io(window.location.hostname)});
    }
  }

  render() {
    return (
      <div className="App">
        <div className="cards">
          <Time socket={this.state.socket} color="rgba(0, 0, 0, 0.25)" />
          <Calendar socket={this.state.socket} color="rgb(121, 112, 255)" calendar="7afrbvotf8p1qcjcpbqp2639as@group.calendar.google.com" />
          <Calendar socket={this.state.socket} color="rgb(253, 147, 72)" calendar="nick@iamnickvolpe.com" />
          <Calendar socket={this.state.socket} color="rgb(210, 102, 255)" calendar="jenn.sager@gmail.com" />
          <Weather socket={this.state.socket} color="rgba(0, 0, 0, 0.25)" />
          <Subway socket={this.state.socket} color="rgba(0, 0, 0, 0.25)" />
          <Feed socket={this.state.socket} color="white" />
        </div>
        <div className="cover"></div>
      </div>
    )
  }
}

export default App;
