import React, { Component } from 'react';
import './Feed.css';
import io from "socket.io-client";
import _ from "lodash";
import Slider from 'react-slick';

class Feed extends Component {
    state = {
        feed: {},
        updated: ""
    }
    socket = io('localhost:3001');

    componentDidMount() {
        var that = this;
        this.socket.on("data", function (data) {
            if (data.feed) {
                that.setState({ 
                    updated: data.feed.timestamp,
                    feed: data.feed.body 
                });
            }
        });
    }

    render() {
        var feedItems = [];
        var settings = {
            dots: false,
            infinite: true,
            speed: 500,
            slidesToShow: 1,
            slidesToScroll: 1
          };
        if (!_.isEmpty(this.state.feed)) {
            feedItems = this.state.feed.items;
        }
        return (
            <div style={{ backgroundColor: this.props.color }} className="module">
                <Slider {...settings}>
                    <div>
                    <h3>1</h3>
                    </div>
                    <div>
                    <h3>2</h3>
                    </div>
                    <div>
                    <h3>3</h3>
                    </div>
                    <div>
                    <h3>4</h3>
                    </div>
                    <div>
                    <h3>5</h3>
                    </div>
                    <div>
                    <h3>6</h3>
                    </div>
                </Slider>
            </div>
        );
    }
}

export default Feed;
