import React, { Component } from 'react';

class Remote extends Component {
    constructor(props) {
        super(props);
        this.changeImage = this.changeImage.bind(this);
        this.changeOpacity = this.changeOpacity.bind(this);
    }

    changeImage() {
        fetch(
            "/api/randomize-background", {
                credentials: "same-origin",
                headers: { "Content-Type": "application/json" },
                method: "POST",
                body: JSON.stringify({q: "sunset"})
            }
        ).then(function(response) {
            console.log(response);
        }).catch(function (error) {
            console.log(error);
        });
    }

    changeOpacity(opacity) {
        fetch(
            "/api/display", {
                credentials: "same-origin",
                headers: { "Content-Type": "application/json" },
                method: "POST",
                body: JSON.stringify({opacity: opacity})
            }
        ).then(function(response) {
            console.log(response);
        }).catch(function (error) {
            console.log(error);
        });
    }

    render() {
        return (
            <div>
                <h1>Remote</h1>
                <input placeholder="Search Images..." type="text"></input>
                <button onClick={this.changeImage}>Change Image</button>
                <button onClick={() => this.changeOpacity("0")}>0%</button>
                <button onClick={() => this.changeOpacity(".5")}>50%</button>
                <button onClick={() => this.changeOpacity("1")}>100%</button>
            </div>
        )
    }
}

export default Remote;
