import React, { Component } from 'react';
import 'normalize.css';
import './App.scss';
import Weather from './modules/Weather.js';
import Time from './modules/Time.js';
import Calendar from './modules/Calendar.js';
import Feed from './modules/Feed.js';
import Subway from './modules/Subway.js';
import io from "socket.io-client";
import { BrowserRouter as Router, Route, Link } from "react-router-dom";

function Home() {
  return (
    <div>
      sdfsdf
    </div>
  )
}

function Remote() {
  return (
    <div>
      <h2>Remote</h2>
    </div>
  );
}

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      background: "",
      opacity: ""
    }
    this.returnSocket = this.returnSocket.bind(this);
  }

  componentDidMount() {
    var that = this;
    this.returnSocket().on("display", function (data) {
      if (data.background) {
        that.setState({ background: data.background });
      }
      if (data.opacity) {
        that.setState({ opacity: data.opacity });
      }
    });
  }

  returnSocket() {
    if (process.env.NODE_ENV === "development") {
      return io("localhost:3000");
    } else {
      return io(window.location.hostname);
    }
  }

  render() {
    return (
      <Router>
        <div>
          <Route exact path="/" component={Home} />
          <Route path="/remote" component={Remote} />
        </div>
      </Router>
    )
  }
}

export default App;
