import React, { Component } from 'react';
import Moment from "react-moment";
import './Time.scss';

class Time extends Component {
  componentDidMount() {
    var canvas = this.refs.canvas;
    canvas.width = 2000;
    canvas.height = 2000;
    canvas.style.width = "80%";
    canvas.style.height = "80%";

    const ctx = this.refs.canvas.getContext('2d');
    var radius = canvas.height / 4;
    ctx.scale(2, 2);
    ctx.translate(radius, radius);
    radius = radius * 0.8;

    setInterval(function () {
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      ctx.save();
      drawNumbers(ctx, radius);
      drawSeasons(ctx, radius);
      drawTime(ctx, radius);
      ctx.restore();
    }, 1000);

    function drawNumbers(ctx, radius) {
      var ang;
      ctx.font = radius * 0.15 + "px arial";
      ctx.fillStyle = "white";
      ctx.textBaseline = "middle";
      ctx.textAlign = "center";
      var months = [
        "Jan",
        "•",
        "•",
        "Apr",
        "•",
        "•",
        "Jul",
        "•",
        "•",
        "Oct",
        "•",
        "•"
      ];
      months.forEach(function (month, num) {
        ang = num * Math.PI / 6;
        ctx.rotate(ang);
        ctx.translate(0, -radius * 0.75);
        ctx.rotate(-ang);
        ctx.fillText(month, 0, 0);
        ctx.rotate(ang);
        ctx.translate(0, radius * 0.75);
        ctx.rotate(-ang);
      });
    }

    function getPosition(ms) {
      var now = new Date();
      var yearStartMS = new Date(now.getFullYear(), 0, 1).getTime();
      var yearEndMS = new Date(now.getFullYear(), 11, 31).getTime();

      var totalDistanceMS = yearEndMS - yearStartMS;
      var totalDistanceRadians = 2 * Math.PI;

      var distanceMS = ms - yearStartMS;

      var distanceRatio = distanceMS / totalDistanceMS;

      return totalDistanceRadians * distanceRatio - (.5 * Math.PI);
    }

    function getRadians(start, end) {
      var now = new Date();
      var startMS = new Date(now.getFullYear(), 3, 1).getTime();

      var yearStartMS = new Date(now.getFullYear(), 0, 1).getTime();
      var yearEndMS = new Date(now.getFullYear(), 11, 31).getTime();

      var totalDistanceMS = yearEndMS - yearStartMS;
      var totalDistanceRadians = 2 * Math.PI;

      var startDistanceMS = start - startMS;
      var endDistanceMS = end - startMS;

      var startDistanceRatio  =  startDistanceMS / totalDistanceMS;
      var endDistanceRatio  =  endDistanceMS / totalDistanceMS;

      var startRadians = totalDistanceRadians * startDistanceRatio;
      var endRadians = totalDistanceRadians * endDistanceRatio;

      return {
        start: startRadians,
        end: endRadians
      }
    }

    function drawSeasons(ctx, radius) {
      var now = new Date();
      var winterStartMS = new Date(now.getFullYear() -1, 11, 21).getTime();
      var winterEndMS = new Date(now.getFullYear(), 2, 20).getTime();

      var springStartMS = new Date(now.getFullYear(), 2, 20).getTime();
      var springEndMS = new Date(now.getFullYear(), 5, 21).getTime();

      var summerStartMS = new Date(now.getFullYear(), 5, 21).getTime();
      var summerEndMS = new Date(now.getFullYear(), 8, 23).getTime();

      var fallStartMS = new Date(now.getFullYear(), 8, 23).getTime();
      var fallEndMS = new Date(now.getFullYear() -1, 11, 21).getTime();

      ctx.beginPath();
      ctx.strokeStyle= "#8FE4FF";
      ctx.lineWidth = radius * 0.06;
      ctx.arc(0, 0, radius, getPosition(winterStartMS), getPosition(winterEndMS) -0.05);
      ctx.stroke();
      ctx.closePath();

      /*ctx.beginPath();
      ctx.strokeStyle= "#BCFF79";
      ctx.lineWidth = radius * 0.06;
      ctx.arc(0, 0, radius, getRadians(springStartMS, springEndMS).start, getRadians(springStartMS, springEndMS).end -0.05);
      ctx.stroke();
      ctx.closePath();

      ctx.beginPath();
      ctx.strokeStyle= "#FFED48";
      ctx.lineWidth = radius * 0.06;
      ctx.arc(0, 0, radius, getRadians(summerStartMS, summerEndMS).start, getRadians(summerStartMS, summerEndMS).end -0.05);
      ctx.stroke();
      ctx.closePath();

      ctx.beginPath();
      ctx.strokeStyle= "#FFBC7E";
      ctx.lineWidth = radius * 0.06;
      ctx.arc(0, 0, radius, getRadians(fallStartMS, fallEndMS).start, getRadians(fallStartMS, fallEndMS).end -0.05);
      ctx.stroke();
      ctx.closePath();*/
    }

    function drawTime(ctx, radius) {
      var now = new Date();
      var nowMS = now.getTime();
      var nowMS = new Date(now.getFullYear(), 9, 1).getTime();

      ctx.beginPath();
      ctx.strokeStyle = "white";
      ctx.lineWidth = radius * 0.02;
      ctx.lineCap = "round";
      ctx.moveTo(0, 0);
      ctx.rotate(getPosition(nowMS) + (.5 * Math.PI));
      ctx.lineTo(0, -radius * 0.9);
      ctx.stroke();
      ctx.closePath();

      /*ctx.beginPath();
      ctx.strokeStyle = "rgba(0, 0, 0, 0.75)";
      ctx.rotate(-radians);
      ctx.lineCap = "square";
      ctx.lineWidth = radius * 0.06;
      ctx.arc(0, 0, radius, getRadians(startMS, startMS + elapsedMS).start, getRadians(startMS, startMS + elapsedMS).end);
      ctx.stroke();
      ctx.closePath();*/
    }
  }

  render() {
    return (
      <div style={{ backgroundImage: `url(${this.props.image})`, backgroundColor: this.props.color }} className="module time">
        <div className="content">
          <div className="time">
            <Moment format="h:mm" /><span className="ampm"><Moment format="A" /></span>
          </div>
          <div className="date">
            <Moment format="dddd, MMMM Do" />
          </div>
          <div className="clock"><canvas ref="canvas"></canvas></div>
        </div>
      </div>
    );
  }
}

export default Time;
